mapper.select:
		SELECT * 
		FROM (
					SELECT A.*, rownum recordNum 
					FROM (
								SELECT regidate, chargePoint, paymentType
								FROM chargeLog
								WHERE id=#{loginId} AND 
									trunc(regidate) between #{fromDate} and #{toDate}
    						ORDER BY regidate DESC
					) A
		)
		WHERE recordNum between #{startRecord} and #{endRecord}

