mapper.update:
UPDATE audioboard
	SET audiotitle=#{audiotitle}, artistname=#{artistname}, contents=#{contents},
		audiofilename=#{audiofilename}, imagename=#{imagename}, category=#{category}
		WHERE audio_idx=#{audio_idx} AND id=#{id}

mapper.delete:
DELETE FROM m_comment
WHERE
comment_idx=#{comment_idx} AND id=#{id}
