mapper.select:
select * from (select a.*,rownum as n from audioboard a Whereregidate<=sysdate-#{0} and regidate>=sysdate-#{1} 
order by (((like_count/(select sum(like_count)+1 from audioboard))+(play_count/(select sum(play_count)+1 from audioboard)))*100 ) DESC) 
where n>=#{2} and n<=#{3}

select * from (select a.*,rownum as n from audioboard a Whereregidate<=sysdate-#{0} and regidate>=sysdate-#{1} 
and party=#{4} order by (((like_count/(select sum(like_count)+1 from audioboard))+(play_count/(select sum(play_count)+1 from audioboard)))*100 ) DESC) 
where n>=#{2} and n<=#{3}
